import React, {useState} from "react";
import "./styles.css";

export default function App() {

  const [message, setMessage] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);

  const maxLength = 60;

  const updateMessage = (e) => {
    setMessage(e.target.value);
  };

  return (
    <div className="App">
      <div className="status-update">
        <h1>Super Short Status!</h1>
        <p>Tell us how you are doing in 60 characters or less!</p>
        <form>
          <textarea className={message.length > maxLength ? className="too-long" : className=""} placeholder="What's good?" value={message} onChange={updateMessage} />
          <button type="submit" disabled={message.length > maxLength}>Tell the world!</button>
        </form>
      </div>
    </div>
  );
}
